import React, { useState } from 'react';
import axios from 'axios';

const Meteo = () => {
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState(null);

    const recupererMeteo = async () => {
        const API_KEY = '4985d1befe2b5f951527df6e5d346497'; // Remplacez par votre clé API réelle
        const URL = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`;

        try {
            const response = await axios.get(URL);
            setWeather(response.data);
        } catch (error) {
            console.error('Erreur lors de la récupération des données météo :', error);
            setWeather(null); // Réinitialiser en cas d'erreur
        }
    };

    return (
        <div>
          <input 
            type="text" 
            value={city} 
            onChange={(e) => setCity(e.target.value)} 
            placeholder="Entrer une ville..."
          />
          <button onClick={recupererMeteo}>Afficher la météo</button>
    
          {weather ? (
            <div>
              <h3>{weather.name}</h3>
              <p>Température : {weather.main.temp}°C</p>
              <p>Météo : {weather.weather[0].description}</p>
            </div>
          ) : (
            <p>Aucune donnée disponible.</p>
          )}
        </div>
    );
};

export default Meteo;
